import React, { Component } from 'react';
import axios from 'axios';

class Home extends Component {
    state = {
        players: [ ]
    }
    componentDidMount() {
        axios.get('https://eurosportdigital.github.io/eurosport-web-developer-recruitment/headtohead.json')
        .then(res => {
          this.setState({
              players: res.data.players
          })
        })
        .catch(error => {
          console.log(error);
        });
      }
    render() {
        const { players } = this.state;
        const playerList = players.length ? (
            players.map(player=>{
                return(

                        <div className="col s12 m5">
                            <div className="card" key={player.shortname}>
                                <div className="card-image">
                                    <img src={player.picture} width='50%'/>
                                    <span className="card-title">{player.shortname}</span>
                                </div>
                                <div className="card-content">
                                    <p></p>
                                </div>
                                <div className="card-action">
                                    <a href="#">View details</a>
                                </div>
                            </div>
                        </div>
                )
            })
        ) : (
            <div className="center">No players yet</div>
        )
        return(
            <div className="container home row">
                {playerList}
            </div>
        )
    }
}

export default Home;
