import { FETCH_PLAYER } from './types';
import axios from 'axios';

const apiUrl = 'https://eurosportdigital.github.io/eurosport-web-developer-recruitment/headtohead.json';

export const fetchPlayers = (players) => {
    return {
      type: FETCH_PLAYER,
      players
    }
  };

  export const fetchAllPlayers = () => {
    return (dispatch) => {
      return axios.get(apiUrl)
        .then(response => {
          dispatch(fetchPlayers(response.data))
        })
        .catch(error => {
          throw(error);
        });
    };
  };
