import React, { Component } from 'react';
import axios from 'axios';

class Home extends Component {
    state = {
        players: [ ]
    }
    componentDidMount() {
        axios.get('https://eurosportdigital.github.io/eurosport-web-developer-recruitment/headtohead.json')
        .then(res => {
          this.setState({
              players: res.data.players
          })
        })
        .catch(error => {
          console.log(error);
        });
      }
    render() {
        const { players } = this.state;

        const playerList = players.length ? (
            players.map(player=>{
                return(
                        <div className="col s12 m6" key={player.shortname}>
                            <div className="card horizontal">
                                <div className="card-image">
                                    <img src={player.picture} />
                                </div>
                                <div className="card-stacked">
                                    <div className="card-content left-align">
                                        <div className="row center-align"><h4 className="blue-text text-darken-4">{player.shortname}</h4></div>
                                        <div className="row">
                                            <div className="col s6">
                                                {player.firstname}  {player.lastname}
                                            </div>
                                            <div className="col s6 grey-text">
                                                Rank : {player.data.rank}
                                            </div>
                                        </div>
                                        <div className="row">
                                            <div className="col s6">
                                               Age : {player.data.age}
                                            </div>
                                            <div className="col s6">
                                            <img src={player.country.picture} width='25%'/>
                                            </div>
                                        </div>
                                        <div className="row">
                                            <div className="col s6">
                                                Weight : {player.data.weight/1000}kg
                                            </div>
                                            <div className="col s6">
                                                Height : {player.data.height}cm
                                            </div>
                                        </div>
                                        <div className="row">

                                            {player.data.last.map((stats) => {
                                                let win;
                                                let lose;
                                                for(stat in stat){
                                                    if(stat == 1){win+=1;}
                                                    if(stat == 0){lose+=1;}
                                                }
                                                console.log(win, lose);
                                                return (
                                                    <div className="col s2"></div>
                                                );
                                            })}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                )
            })
        ) : (
            <div className="center">No players yet</div>
        )
        return(
            <div className="container home row center">
                {playerList}
            </div>
        )
    }
}

export default Home;
