import React, { Component } from 'react'
import axios from 'axios'

class Player extends Component {
    state = {
        player: null
    }
    componentDidMount(){
        let id = this.props.match.params.id;
        axios.get('https://eurosportdigital.github.io/eurosport-web-developer-recruitment/headtohead.json' + id)
            .then(res=>{
               this.setState({
                   post:res.data.players
               }) 
               console.log(res);
            })
    }
    render() {
        return (
            <div className="container">
                <h4>{this.state.id}</h4>
            </div>
        )
    }
}
export default Player
