import React, { Component } from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';

class Home extends Component {
    state = {
        players: [ ]
    }
    componentDidMount() {
        axios.get('https://eurosportdigital.github.io/eurosport-web-developer-recruitment/headtohead.json')
        .then(res => {
          this.setState({
              players: res.data.players
          })
        })
        .catch(error => {
          console.log(error);
        });
      }
    render() {
        const { players } = this.state;
        const playerList = players.length ? (
            players.map(player=>{
                return(
                        <div className="col s12 m6" key={player.shortname}>
                            <div className="card horizontal">
                                <div className="card-image">
                                    <img className=" col s2 circle responsive-img" src={player.picture} height='100%'/>
                                </div>
                                <div className="card-stacked">
                                    <div className="card-content">

                                    </div>
                                    <div className="card-action">
                                    <Link to="/" className="right blue-text text-darken-4">More</Link>
                                    </div>
                                </div>
                            </div>
                        </div>
                )
            })
        ) : (
            <div className="center">No players yet</div>
        )
        return(
            <div className="container home row center">
                {playerList}
            </div>
        )
    }
}

export default Home;
